-- Spots 데이터 삽입
INSERT INTO spots (spot_id, market_id, name, category, description, image_id, latitude, longitude) VALUES
-- 대전 중앙 시장 (market_id: 1) - spots 1-9
(1, 1, '개천식당', '식당', '자극적이지 않고 담백한 맛이 일품인 이북식 손만두 전문점입니다. ''생활의 달인''에도 소개된 숨은 맛집으로, 슴슴한 만두국이 별미입니다.', NULL, 36.3294, 127.4303),
(2, 1, '스모프치킨', '식당', '매콤달콤한 특제 간장 소스로 유명한, 대전 현지인들이 사랑하는 치킨 맛집입니다. 바삭한 튀김과 어우러지는 중독성 있는 맛을 경험해 보세요.', NULL, 36.3308, 127.4325),
(3, 1, '서울치킨', '식당', '옛날 통닭의 맛과 정취를 그대로 간직한 노포입니다. 바삭한 후라이드 치킨과 함께, 이곳에서만 맛볼 수 있는 특별 메뉴 ''닭내장탕''도 인기가 많습니다.', NULL, 36.3297, 127.4318),
(4, 1, '싸지롱', '카페', '시장 골목에 비밀스럽게 숨어있는 핸드드립 커피 전문점. 독특하고 고즈넉한 분위기 속에서 수준 높은 커피를 즐길 수 있는 특별한 공간입니다.', NULL, 36.3296, 127.4303),
(5, 1, '성심당문화원', '카페', '대전의 명물 성심당이 운영하는 복합문화공간. 성심당 빵과 함께 음료를 즐기고, 다양한 굿즈와 전시를 구경할 수 있는 관광 필수 코스입니다.', NULL, 36.3276, 127.4281),
(6, 1, '미들커피', '카페', '전통시장 골목과 현대적인 인테리어의 조화가 돋보이는 감성 카페입니다. SNS 핫플레이스', NULL, 36.3290, 127.4295),
(7, 1, '청년구단', '즐길거리', '청년 셰프들이 운영하는 개성 넘치는 푸드코트. 다양한 음식과 함께 대형 스크린으로 스포츠 경기를 관람할 수 있는 시장 속 젊음의 공간입니다.', NULL, 36.3308, 127.4325),
(8, 1, '동구夜 놀자 야시장', '즐길거리', '매주 금, 토요일 저녁에 열리는 활기찬 야시장. 맛있는 길거리 음식과 다채로운 공연이 어우러져 시장의 밤을 축제처럼 만들어 줍니다.', NULL, 36.3300, 127.4330),
(9, 1, '중앙쇼핑타워', '즐길거리', '시장 내에 위치한 현대식 쇼핑몰. 혼수, 그릇, 침구류 등을 전문적으로 판매하며, 쾌적한 환경에서 쇼핑을 즐길 수 있습니다.', NULL, 36.3308, 127.4325),

-- 대전 문창 시장 (market_id: 2) - spots 10-18
(10, 2, '감자바위골', '식당', '얼굴만 한 크기의 ''대왕 감자전''으로 전국적인 명성을 얻은 곳. 겉은 바삭, 속은 촉촉한 감자전과 푸짐한 칼국수는 문창시장 방문의 이유가 됩니다.', NULL, 36.3181, 127.4293),
(11, 2, '진주냉면 이설옥', '식당', '해물 육수의 깊은 감칠맛이 특징인 ''진주냉면'' 전문점. 평범한 냉면이 아닌 특별한 맛을 찾는 미식가들에게 추천하는 곳입니다.', NULL, 36.3188, 127.4278),
(12, 2, '문창동 광천순대', '식당', '진한 국물과 푸짐한 건더기를 자랑하는 순대국밥 맛집. 저렴한 가격으로 든든한 한 끼를 해결할 수 있어 현지인들에게 인기가 높습니다.', NULL, 36.3172, 127.4281),
(13, 2, '베베꽈 배기', '카페', '독특한 맛의 수제 꼬임도넛으로 유명한 디저트 카페. 다양한 맛의 도넛과 함께 즐기는 커피가 일품입니다.', NULL, 36.3178, 127.4287),
(14, 2, '카페 그림', '카페', '문창시장 근처에 위치한 아늑한 분위기의 카페. 전통과 현대가 조화를 이루는 독특한 인테리어가 특징입니다.', NULL, 36.3194, 127.4291),
(15, 2, '영순카페', '카페', '오랜 역사를 가진 전통 카페. 시장의 정취를 느낄 수 있는 분위기와 함께 즐기는 커피가 특별합니다.', NULL, 36.3184, 127.4279),
(16, 2, '문창시장 떡집 거리', '즐길거리', '50년 전통의 떡 전문점들이 모여있는 거리. 다양한 종류의 떡과 전통 음식을 맛볼 수 있는 대전의 떡 성지입니다.', NULL, 36.3175, 127.4292),
(17, 2, '한화생명 이글스파크', '즐길거리', '한화 이글스의 홈구장. 야구 경기 관람뿐만 아니라 구장 내 다양한 시설을 즐길 수 있는 복합 스포츠 공간입니다.', NULL, 36.3172, 127.4292),
(18, 2, '보문산 행복 숲 둘레길', '즐길거리', '도시 속 자연을 만날 수 있는 둘레길. 산책과 등산을 즐기며 도시의 아름다운 전경을 감상할 수 있습니다.', NULL, 36.3119, 127.4221),

-- 대전 역전시장 (market_id: 3) - spots 19-27
(19, 3, '김화칼국수', '식당', '들깨가루가 듬뿍 들어가 고소한 국물이 일품인 칼국수집. 대전역과 가까워 여행객들이 대전의 맛을 제대로 느낄 수 있는 곳으로 특히 인기가 많습니다.', NULL, 36.3315, 127.4348),
(20, 3, '호돌이만두', '식당', '저렴한 가격과 변함없는 맛으로 오랫동안 사랑받아온 동네 만두 맛집. 출출할 때 부담 없이 즐기기 좋은 최고의 간식입니다.', NULL, 36.3330, 127.4363),
(21, 3, '별난집', '식당', '매콤한 양념의 ''두부두루치기''가 대표 메뉴인 대전 현지인 맛집. 중독성 있는 맛으로 대전의 독특한 향토 음식을 경험하고 싶다면 꼭 방문해야 할 곳입니다.', NULL, 36.3293, 127.4343),
(22, 3, '챔프스페이스커피로스터스', '카페', '소제동의 핫플레이스. 직접 로스팅한 스페셜티 커피와 맛있는 도넛을 함께 즐길 수 있는 감각적인 공간입니다.', NULL, 36.3343, 127.4371),
(23, 3, '흐그이므자', '카페', '''흑임자''라는 독특한 이름의 모던한 감성 카페. 귀여운 디저트와 깔끔한 인테리어로 인기가 많습니다.', NULL, 36.3240, 127.4262),
(24, 3, '화양연화', '카페', '한옥의 멋을 느낄 수 있는 분위기 좋은 카페. 아늑한 테라스에 앉아 여유로운 시간을 보내기 좋습니다.', NULL, 36.3340, 127.4377),
(25, 3, '소제동 철도관사촌', '즐길거리', '오래된 철도 노동자들의 관사가 보존되어 있는 역사적인 마을. 과거와 현재가 공존하는 골목을 거닐며 숨겨진 카페와 맛집을 발견하는 재미가 있습니다.', NULL, 36.3356, 127.4337),
(26, 3, '꿈돌이 하우스', '즐길거리', '대전의 마스코트 ''꿈돌이''를 테마로 한 관광안내소 겸 굿즈샵. 여행 정보를 얻고 귀여운 기념품도 구매할 수 있는 재미있는 공간입니다.', NULL, 36.3311, 127.4340),
(27, 3, '대전트래블라운지', '즐길거리', '여행자들을 위한 공식적인 쉼터이자 안내소. 짐 보관, 여행 상담 등 실질적인 편의를 제공하여 대전 여행의 훌륭한 시작점이 되어 줍니다.', NULL, 36.3296, 127.4328);
